<?php		session_start();		$user = $_SESSION['user'];		$con = mysql_connect("localhost","root","");		$database = "webapp";		$db =  mysql_select_db($database,$con);		$quantite = sprintf("select qte ,prix  							from commander							inner join menu							on commander.plat = menu.nom							where commander.username = '%s'",$user);		$result2 = mysql_query($quantite,$con);				$ligne = mysql_num_rows($result2);		$price = 1;		$total = 0;		for($i=0; $i < $ligne ; $i++)		{		$resarray = mysql_fetch_array($result2);						for($j=0; $j<2; $j++)			{						$price = $price * $resarray[$j];						}			$total = $total + $price;			$price = 1;									}		echo $total;		?>